                DayPilotCalendar.editing = null;

                //console.log(edit.parentNode);

                edit.onblur = null;

                //if (edit.)
                DayPilot.de(edit);
                //edit.parentNode.removeChild(edit);

                if (oldText === newText) {
                    return;
                }

                object.style.display = 'none';
