                calendar.debug.message("mousemove/moving start coords: " + DayPilotMonth.movingEvent.coords.x + " " + DayPilotMonth.movingEvent.coords.y);
                calendar.debug.message("mousemove/current coords: " + coords.x + " " + coords.y);
                
