                info.event = e;
                info.start = newStart;
                info.end = newEnd;
                args.multiresize.splice(0, 0, info);

                if (typeof calendar.onEventResize === 'function') {
                    calendar.onEventResize(args);
                }

                if (!args.async) {
                    doit();
                }
                else {
                    if (calendar.blockOnCallBack) {
                        overlay.show();
                    }
                }

